mapper.update:
update bbs6 
	set bbs_re_seq = bbs_re_seq + 1 
where bbs_re_ref = #{bbs_re_ref} and bbs_re_seq > #{bbs_re_seq}

update bbs6 set 
	bbs_subject = #{bbs_subject}, bbs_content = #{bbs_content}, 
	bbs_file = #{bbs_file, jdbcType=VARCHAR}, bbs_original = #{bbs_original, jdbcType=VARCHAR}   
where bbs_num = #{bbs_num}

mapper.select:
select * from (select rownum rnum, bbs_num, bbs_name, bbs_pass, bbs_subject, bbs_content, bbs_file, bbs_re_ref, bbs_re_lev, bbs_re_seq, bbs_readcount, bbs_date, bbs_original from (select * from bbs6 order by bbs_re_ref desc, bbs_re_seq asc)
bbs_subject like '%'||#{keyword}||'%'
bbs_content like '%'||#{keyword}||'%'
bbs_name like '%'||#{keyword}||'%'
bbs_subject like '%'||#{keyword}||'%' or bbs_content like '%'||#{keyword}||'%'
) where rnum >= #{startrow} and rnum <= #{endrow}
